    jsonb_set(
      jsonb_set(COALESCE(translations,'{}'::jsonb), '{title,hi}', to_jsonb('थ्री-पॉइंटर की कला: बास्केटबॉल शूटिंग मैकेनिक्स की गहराई से पड़ताल'), true),
      '{content,hi}', to_jsonb('थ्री-पॉइंटर आज के आधुनिक बास्केटबॉल में सबसे प्रभावी हथियारों में से एक है। इस शॉट की सफलता केवल दूरी पर निर्भर नहीं करती, बल्कि फुटवर्क, संतुलन, रिलीज़ टाइमिंग, और बॉल की आर्क पर भी समान रूप से निर्भर करती है।\n\nफुटवर्क: स्थिर आधार बनाना अनिवार्य है। शूटर का पहला फोकस स्टांस होना चाहिए—दोनों पैरों का चौड़ाई में लगभग कंधे के बराबर होना, और घुटनों का हल्का मोड़। कैच-एंड-शूट में, पिवट फुट का सही एंगल (लगभग 10–15° अंदर) टॉर्सो को रिम के साथ संरेखित करता है, जिससे रिलीज़ सीधी होती है।\n\nहैंड प्लेसमेंट: ऑफ-हैंड केवल गाइड की तरह काम करे, प्रेशर न डाले। शूटिंग हाथ की कलाई फ्लेक्शन के साथ स्नैप होनी चाहिए ताकि स्पिन कंसिस्टेंट रहे। उंगलियों का स्प्रेड इतना हो कि कंट्रोल बने रहे, लेकिन ओवरग्रिप न बने।\n\nरिलीज़ और आर्क: आदर्श रिलीज़ पॉइंट माथे के ऊपर, कोहनी 90° के आसपास। हाई-आर्क (लगभग 45–50°) थ्री-पॉइंटर के लिए बेहतर है क्योंकि यह रिसीवर एरिया बढ़ाता है और रिम-इन चांस बढ़ता है।\n\nफ़ॉलो-थ्रू: हथेली नीचे की ओर, उंगलियाँ रिम की तरफ—क्लासिक “गूज़-नेक”—स्टेबल फ़िनिश देता है। लैंडिंग पर दोनों पैरों का संतुलन रीसेट करें ताकि अगले पोज़ेशन में तैयार रहें।\n\nशॉट चयन: ट्रांज़िशन में जल्दी थ्री लेने से पहले डिफेंस की स्थिति पढ़ें; कॉन्टेस्टेड थ्री का इफिशिएंसी घटता है। कॉर्नर थ्री का दूरी कम होती है, इसलिए यह हाई-वैल्यू स्पॉट है।\n\nड्रिल्स: (1) कैच-एंड-शूट 5 स्पॉट्स × 10 रिप्स। (2) वन-ड्रिबल पुल-अप थ्री—लेटरल और फॉरवर्ड। (3) गेम-स्पीड सेट्स: शॉट-क्लॉक पर 8–10 सेकंड में डिसीजन मेकिंग।\n\nनिष्कर्ष: थ्री-पॉइंटर केवल दूरी का शॉट नहीं, बल्कि मैकेनिक्स की एक कॉर्डिनेटेड श्रृंखला है। बेसिक सिद्धांत—स्टांस, हैंड-प्लेसमेंट, टाइमिंग, और फ़ॉलो-थ्रू—पर महारत थ्री-पॉइंटर को निरंतर और विश्वसनीय बनाती है।'), true),
    '{excerpt,hi}', to_jsonb('थ्री-पॉइंटर की तकनीक, फुटवर्क, रिलीज़ और आर्क—हर पहलू का विश्लेषण। बेहतर मैकेनिक्स से शॉट की स्थिरता और इफिशिएंसी बढ़ती है।'), true),
