SET translations = jsonb_set(
  jsonb_set(
    jsonb_set(
      jsonb_set(COALESCE(translations,'{}'::jsonb), '{title,hi}', to_jsonb('फॉल्स नाइन: आधुनिक फुटबॉल में भूमिका और रणनीति'), true),
      '{content,hi}', to_jsonb('फॉल्स नाइन एक ऐसा सेंटर-फॉरवर्ड है जो पारंपरिक नंबर 9 की तरह बैकलाइन पर टिके रहने के बजाय गहराई में ड्रॉप करता है। इसका उद्देश्य मिडफ़ील्ड में संख्यात्मक बढ़त बनाना और डिफेंसिव स्ट्रक्चर में भ्रम पैदा करना है।\n\nपोज़िशनिंग और मूवमेंट: फॉल्स नाइन अक्सर हाफ-स्पेस में ड्रॉप करता है, जिससे सेंटर-बैक और डिफेंसिव मिडफील्डर के बीच निर्णय लेने का संकट पैदा होता है—कौन ट्रैक करेगा? इस गैप‑क्रिएशन से विंगर्स या इन्वर्टेड फुलबैक आगे की रन बना सकते हैं।\n\nलिंक-प्ले और कंबिनेशंस: फॉल्स नाइन का पहला कार्य बैक‑टू‑गोल रिसीविंग और ले‑ऑफ पास है। वन‑टच कंबिनेशंस से टेम्पो बढ़ता है और मिडफील्ड ट्रायंगल्स (8‑10‑W) सक्रिय होते हैं।\n\nटीम स्ट्रक्चर पर प्रभाव: पोज़ेशन टीमों में, यह रोल रोटेशनल फ्लुइडिटी लाता है—10, विंगर्स, और फुलबैक के साथ डायनेमिक इंटरचेंज। ट्रांज़िशन में, फॉल्स नाइन का ड्रॉप डिफेंसिव रिस्टोरेशन में मदद करता है।\n\nकाउंटर‑मेज़र्स: अच्छे डिफेंस फॉल्स नाइन को हैंड‑ऑफ करते हैं—CB प्रेस नहीं करता, D‑Mid ट्रैक करता है—ताकि बैकलाइन डिसऑर्गनाइज़ न हो। ज़ोनल डिसिप्लिन और कम्युनिकेशन महत्वपूर्ण हैं।\n\nनिष्कर्ष: फॉल्स नाइन एक टैक्टिकल डिवाइस है जो पोज़िशनल प्ले में ओवरलोड्स और डिसऑर्गनाइज़ेशन पैदा करता है। सही टीम स्ट्रक्चर, रोटेशंस और टाइमिंग के साथ यह आधुनिक फुटबॉल में अत्यंत प्रभावी हो सकता है।'), true),
    '{excerpt,hi}', to_jsonb('फॉल्स नाइन की अवधारणा, पोज़िशनिंग, मूवमेंट और टीम संरचना पर प्रभाव — एक व्यावहारिक विश्लेषण।'), true),
  '{category,hi}', to_jsonb('फुटबॉल'), true)
WHERE slug = 'false-nine';

